pragma solidity ^0.8.17;

contract BIP39 {
    string constant public language = "english";
    string[2048] public wordlist;
    bool public finalized;
    address private deployer;

    constructor() {
        deployer = msg.sender;
    }

    modifier onlyDeployer {
        require(msg.sender == deployer, "only deployer");
        _;
    }

    function commitWords(string[] memory _wordsChunk, uint256 _offset) external onlyDeployer {
        require(!finalized, "finalized");
        require(_offset + _wordsChunk.length - 1 < 2048, "index too high");
        for (uint256 i = 0; i < _wordsChunk.length; i++) {
            wordlist[_offset + i] = _wordsChunk[i];
        }
    }

    function finalizeWords() external onlyDeployer {
        finalized = true;
    }

    function getWordlist() external view returns (string[2048] memory) {
        return wordlist;
    }

    /*//////////////////////////////////////////////////////////////
                                BIP39
    //////////////////////////////////////////////////////////////*/

    function entropyToMnemonic(bytes memory entropy) public pure returns (uint[] memory) {
        bytes32 hashedEntropy = sha256(entropy);
        string memory binaryEntropy = "";
        for (uint i = 0; i < entropy.length; i++) {
            binaryEntropy = string(abi.encodePacked(binaryEntropy, uintToBinaryString(uint8(entropy[i]), 8)));
        }
        string memory binaryChecksum = substring(uintToBinaryString(uint256(hashedEntropy), 256), 0, entropy.length * 8 / 4);
        string memory binaryString = string(abi.encodePacked(binaryEntropy, binaryChecksum));

        uint mnemonicLength = (entropy.length * 8 + entropy.length / 4 + 7) / 11;

        uint[] memory mnemonicIndices = new uint[](mnemonicLength);
        for (uint i = 0; i < mnemonicLength; i++) {
            uint index = binaryToUint(substring(binaryString, i * 11, min((i+1) * 11, bytes(binaryString).length)));
            mnemonicIndices[i] = index;
        }
        return mnemonicIndices;
    }


    function entropyToMnemonicString(bytes memory entropy) public view returns (string[] memory) {
        return indicesToWords(entropyToMnemonic(entropy));
    }

    function mnemonicToEntropy(uint[] memory wordIndices) public pure returns (bytes memory) {
        require(wordIndices.length >= 3 && wordIndices.length <= 24 && wordIndices.length % 3 == 0, "Invalid word count");

        uint concatLenBits = wordIndices.length * 11;
        bytes memory concatBits = new bytes(concatLenBits);

        for (uint wordIndex = 0; wordIndex < wordIndices.length; wordIndex++) {
            uint ndx = wordIndices[wordIndex];

            for (uint i = 0; i < 11; i++) {
                bool isOne = (ndx & (1 << (10 - i))) != 0;
                concatBits[(wordIndex * 11) + i] = isOne ? bytes1(uint8(1)) : bytes1(uint8(0));
            }
        }

        uint checksumLengthBits = concatLenBits / 33;
        uint entropyLengthBits = concatLenBits - checksumLengthBits;

        bytes memory entropy = new bytes(entropyLengthBits / 8);
        for (uint i = 0; i < entropy.length; i++) {
            for (uint j = 0; j < 8; j++) {
                if (uint8(concatBits[(i * 8) + j]) == 1) {
                    entropy[i] = bytes1(uint8(entropy[i]) | uint8(1 << (7 - j)));
                }
            }
        }

        bytes32 hashBytes = sha256(entropy);

        for (uint i = 0; i < checksumLengthBits; i++) {
            require(
                uint8(concatBits[entropyLengthBits + i]) == uint8(uint8(hashBytes[i / 8]) >> (7 - i % 8) & 1),
                "Failed checksum"
            );
        }

        return entropy;
    }

    function generateMnemonic(uint256 words) public view returns (uint[] memory) {
        require(words >= 3 && words <= 24 && words % 3 == 0, "Invalid word count");
        bytes memory entropy = generateEntropy(words);
        uint[] memory mnemonicIndices = entropyToMnemonic(entropy);
        return mnemonicIndices;
    }

    function generateMnemonicString(uint256 words) public view returns (string[] memory) {
        return indicesToWords(generateMnemonic(words));
    }

    function indicesToWords(uint256[] memory indices) public view returns (string[] memory) {
        string[] memory words = new string[](indices.length);
        for (uint index; index < indices.length; index++) {
            words[index] = wordlist[indices[index]];
        }
        return words;
    }

    function generateEntropy(uint256 words) public view returns (bytes memory) {
        require(words >= 3 && words <= 24 && words % 3 == 0, "Invalid word count");

        bytes32 totalEntropy = keccak256(abi.encodePacked(
            tx.origin,
            blockhash(block.number - 1),
            block.timestamp,
            gasleft()
        ));

        uint256 bits = words / 3 * 32;
        uint256 bytesLength = bits / 8;

        bytes memory entropy = new bytes(bytesLength);
        for (uint i = 0; i < bytesLength; i++) {
            entropy[i] = totalEntropy[i];
        }
        return entropy;
    }


    /*//////////////////////////////////////////////////////////////
                                Utility
    //////////////////////////////////////////////////////////////*/

    function substring(string memory str, uint startIndex, uint endIndex) internal pure returns (string memory) {
        bytes memory strBytes = bytes(str);
        bytes memory result = new bytes(endIndex - startIndex);
        for (uint i = startIndex; i < endIndex; i++) {
            result[i - startIndex] = strBytes[i];
        }
        return string(result);
    }

    function uintToBinaryString(uint256 x, uint256 length) internal pure returns (string memory) {
        bytes memory result = new bytes(length);
        for (uint i = 0; i < length; i++) {
            uint8 b = uint8(uint(x) >> (length - 1 - i));
            result[i] = b % 2 == 0 ? bytes1(uint8(48)) : bytes1(uint8(49));  // 48 and 49 are '0' and '1' in ASCII
        }
        return string(result);
    }

    function binaryToUint(string memory binaryString) internal pure returns (uint) {
        bytes memory binaryBytes = bytes(binaryString);
        uint result = 0;
        for (uint i = 0; i < binaryBytes.length; i++) {
            if (binaryBytes[i] == '1') {
                result += 2**(binaryBytes.length - i - 1);
            }
        }
        return result;
    }

    function min(uint a, uint b) private pure returns (uint) {
        return a < b ? a : b;
    }
}","keccak256":"0x9992e90d72d191004f38760a81d15f6767b7d8b8bc1b5dfb26ba9d71c0ec2ce8","license":"UNLICENSED"},"src/SeedPoems.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;

import "solmate/src/tokens/ERC721.sol";
import "solmate/src/auth/Owned.sol";
import "solady/src/utils/SSTORE2.sol";
import "./BIP39.sol";
import {Render} from "./libraries/Render.sol";

////////////////////////////////////////////////////////////////////////
//                                                                    //
//                                                                    //
//                                                                    //
//                                                                    //
//                                                                    //
//                                                                    //
//                                                                    //
//                               seek                                 //
//                               poet                                 //
//                               seed                                 //
//                                                                    //
//                                                                    //
//                                                                    //
//                                                                    //
//                                                                    //
//                                                       0xfff.eth    //
//                                                                    //
////////////////////////////////////////////////////////////////////////

contract SeedPoems is ERC721, BIP39, Owned {
    struct Seed {
        uint256[] indices;
        bytes entropy;
        bool bound;
    }
    mapping(uint256 => Seed) public seeds;

    uint256 public totalSupply; // => 2048 words ~ 70~650 / 200-250 realistic
    uint256 public wordPrice = 0.01 ether; // 2048 words => 20.48 ETH

    // Scenario A
    // First 512 free
    // Then 0.01

    // Scenario B
    // First 512 free
    // Next 512 0.005
    // Next 512 0.01
    // Last 512 0.015

    // Scenario C
    // Potential curve
    // first 512 free
    // next 512 0.005
    // next 512 0.01
    // next 256 0.02
    // next 128 0.03
    // next 64 0.04
    // next 32 0.05
    // next 16 0.1
    // next 8 0.2
    // next 6 0.5
    // next 2 1

    bool public locked = true; // prevent minting

    // mapping(uint256 => bool) public isIndexMinted;
    mapping(bytes32 => bool) public isEntropyMinted;
    uint256 totalWords = 0; // total minted words

    address private font;

    constructor() ERC721("Seed Poem", "SEED") Owned(msg.sender) {
        uint256[] memory seedIndices = new uint256[](3);
        seedIndices[0] = 1560; // seek
        seedIndices[1] = 1337; // poet
        seedIndices[2] = 1559; // seed
        _mintPoem(seedIndices, false);
    }

    /*//////////////////////////////////////////////////////////////
                                  Mint
    //////////////////////////////////////////////////////////////*/

    function lock(bool state) external onlyOwner {
        locked = state;
    }

    function mint(uint256[] memory seedIndices) external payable {
        uint seedPrice = wordPrice * seedIndices.length;
        require(!locked, "LOCKED");
        require(msg.value == seedPrice, "PRICE");
        _mintPoem(seedIndices, false);
    }

    // function mintAndBind(uint256[] memory seedIndices, address to) external payable {
    //     // TODO: Verify signature
    //     _mintPoem(seedIndices, true);
    // }

    function _mintPoem(uint256[] memory seedIndices, bool bound) internal {
        uint id = ++totalSupply;

        // Keep track of words minted
        totalWords += seedIndices.length;
        require(totalWords < 2048, "MINTABLE_WORD_LIMIT_REACHED_2048");

        // This generates the entropy from the seed indices
        // and simultaneously validates the seed.
        bytes memory entropy = mnemonicToEntropy(seedIndices);

        // Words can only be minted once
        // for (uint i = 0; i < seedIndices.length; i++) {
        //     require(!isIndexMinted[seedIndices[i]], "WORD_ALREADY_MINTED");
        //     isIndexMinted[seedIndices[i]] = true;
        // }
        // hash entropy to check if it has been minted
        bytes32 entropyHash = keccak256(entropy);
        require(!isEntropyMinted[entropyHash], "MNEMONIC_ALREADY_MINTED");
        isEntropyMinted[entropyHash] = true;

        // Store the seed
        seeds[id] = Seed({
            indices: seedIndices,
            entropy: entropy,
            bound: bound
        });

        // Mint the token
        _mint(msg.sender, id);
    }

    /**
     * @dev Intended to be used offchain
     */
    function getMintedIndices() external view returns (uint256[] memory) {
        uint256[] memory mintedIndicesArray = new uint256[](totalWords);
        uint words = 0;
        for (uint i = 0; i < totalSupply; i++) {
            for (uint j = 0; j < seeds[i].indices.length; j++) {
                mintedIndicesArray[words++] = seeds[i].indices[j];
            }
        }
        return mintedIndicesArray;
    }

    /*//////////////////////////////////////////////////////////////
                                 BINDING
    //////////////////////////////////////////////////////////////*/

    /**
     * This method allows a seed poem to be bound to a specific
     * address that is derived from the seed itself.
     * @param id tokenId of the poem to be bound
     */
    function bind(
        uint256 id,
        address to /*, message, signature, address */
    ) public {
        require(ownerOf(id) == msg.sender, "ONLY_OWNER");
        // TODO: require valid signature signed offchain by the artist's "validator"
        transferFrom(msg.sender, to, id);
        seeds[id].bound = true;
    }

    // Inherit natspec
    function transferFrom(
        address from,
        address to,
        uint256 id
    ) public override {
        require(!seeds[id].bound, "BOUND");
        super.transferFrom(from, to, id);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 id
    ) public override {
        require(!seeds[id].bound, "BOUND");
        super.safeTransferFrom(from, to, id);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        bytes calldata data
    ) public override {
        require(!seeds[id].bound, "BOUND");
        super.safeTransferFrom(from, to, id, data);
    }

    /* /////////////////////////////////////////////////////////////
                                 UTILS
    //////////////////////////////////////////////////////////////*/

    function withdraw(address payable _to) public onlyOwner {
        (bool success, ) = _to.call{value: address(this).balance}("");
        require(success);
    }

    /*//////////////////////////////////////////////////////////////
                                 RENDER
    //////////////////////////////////////////////////////////////*/

    function renderSVG(uint256 _tokenId) external view returns (string memory) {
        return
            Render.renderSVG(
                indicesToWords(seeds[_tokenId].indices),
                seeds[_tokenId].entropy,
                getFont()
            );
    }

    function renderSVGFromWords(
        uint256[] memory seedIndices
    ) external view returns (string memory) {
        bytes memory entropy = mnemonicToEntropy(seedIndices);
        return
            Render.renderSVG(indicesToWords(seedIndices), entropy, getFont());
    }

    function renderSVGBase64(
        uint256 _tokenId
    ) external view returns (string memory) {
        return
            Render.renderSVGBase64(
                indicesToWords(seeds[_tokenId].indices),
                seeds[_tokenId].entropy,
                getFont()
            );
    }

    function renderSVGBase64FromWords(
        uint256[] memory seedIndices
    ) external view returns (string memory) {
        bytes memory entropy = mnemonicToEntropy(seedIndices);
        return
            Render.renderSVGBase64(
                indicesToWords(seedIndices),
                entropy,
                getFont()
            );
    }

    function renderSVGstatic(
        uint256 _tokenId
    ) external view returns (string memory) {
        return
            Render.renderSVGstatic(
                indicesToWords(seeds[_tokenId].indices),
                seeds[_tokenId].entropy,
                getFont()
            );
    }

    function renderSVGstaticFromWords(
        uint256[] memory seedIndices
    ) external view returns (string memory) {
        bytes memory entropy = mnemonicToEntropy(seedIndices);
        return
            Render.renderSVGstatic(
                indicesToWords(seedIndices),
                entropy,
                getFont()
            );
    }

    function renderSVGBase64static(
        uint256 _tokenId
    ) external view returns (string memory) {
        return
            Render.renderSVGBase64static(
                indicesToWords(seeds[_tokenId].indices),
                seeds[_tokenId].entropy,
                getFont()
            );
    }

    function renderSVGBase64staticFromWords(
        uint256[] memory seedIndices
    ) external view returns (string memory) {
        bytes memory entropy = mnemonicToEntropy(seedIndices);
        return
            Render.renderSVGBase64static(
                indicesToWords(seedIndices),
                entropy,
                getFont()
            );
    }

    function tokenURI(
        uint256 _tokenId
    ) public view override returns (string memory) {
        require(_ownerOf[_tokenId] != address(0), "NOT_MINTED");
        return
            Render.tokenURI(
                _tokenId,
                indicesToWords(seeds[_tokenId].indices),
                seeds[_tokenId].entropy,
                getFont()
            );
    }

    ///////////////////////////////////////////////////////////////////////////
    // Font
    ///////////////////////////////////////////////////////////////////////////

    function setFont(string calldata fontString) external onlyOwner {
        font = SSTORE2.write(bytes(fontString));
    }

    function getFont() public view returns (string memory) {
        return string(abi.encodePacked(SSTORE2.read(font)));
    }
}
